mapper.select:
select rei.id, ui.name, ui.mobile, rei.add_time, rei.status, rei.requit_reason, rei.reason, rei.update_time, oi.name product_name, oi.mobile product_mobile, oi.sum, oi.source from jrtd_requit_info rei LEFT JOIN user_info ui ON rei.user_id = ui.id LEFT JOIN order_info oi ON rei.order_id = oi.id where 1 = 1
and ui.name = #{name}
and rei.status = #{status}
and oi.source = #{source}
order by rei.add_time desc limit #{start}, #{size}

mapper.update:
update jrtd_requit_info 
SET status = #{status}
where id = #{id}
